echo "cd /tmp"; cd /tmp; echo "syncing from git"; git clone https://github.com/raptastics/dotfiles.git > /dev/null 2>&1; sync () { if [ -f dotfiles/${1} ]; then if [ -f ~/${1} ]; then output=`diff dotfiles/${1} ~/${1}`; if [ -z "$output" ]; then echo "${1} already in place"; else echo "diff of dotfiles/${1} ~/${1}"; echo "$output"; echo "making a backup and replacing ${1}"; cp ~/${1}{,.bak}; ls ~/${1}.bak ~/${1}; mv dotfiles/${1} ~/${1}; fi; else echo "~/${1} didn't exist, syncing new file in"; mv dotfiles/${1} ~/${1}; fi; else echo "file from git doesn't exist"; fi; }; sync '.bashrc'; sync '.vimrc'; sync '.psqlrc'; rm -rf dotfiles; echo -n "cd "; cd -
